e = 0   \n\n            contents = [\n                ( 'created_at', new_datetime ),\n                ( 'created_at_utc', utc_datetime ), \n                ( 'id_str', tweet['id_str'] ),                \n                ( 'text', tweet['text'] ),\n                ( 'source', tweet['source'] ), \n                ( 'Web', str(isWeb) ),               \n                ( 'Android', str(isAndroid) ), \n                ( 'iPhone', str(isiPhone) ), \n                ( 'tweet_size', str(len( tweet['text'] )) ) \n                ]\n            print(contents)\n            # put LogService\n            logitemList = []\n            logItem = LogItem()\n            logItem.set_time(int(time.time()))\n            logItem.set_contents(contents)\n            logitemList.append(logItem)\n\n            request = PutLogsRequest(PROJECT, LOGSTORE, topic, source, logitemList)\n            response = client.put_logs(request)\n            response.log_print()\n\n    def on_error(self, status):\n        print(status)\n\nif __name__ == '__main__':\n    query = \"#\u9B3C\u6EC5\u306E\u5203\" # \u53D6\u5F97\u3057\u305F\u3044\u7279\u5B9A\u306E\u30AD\u30FC\u30EF\u30FC\u30C9\u3084\u30CF\u30C3\u30B7\u30E5\u30BF\u30B0\n    listener = StdOutListener()\n    twitterStream = Stream(auth, listener)\n    twitterStream.filter(track = [query])\n\n\n")), mdx("p", null, "\u6CE8\u610F\u3068\u3057\u3066\u3001\u3053\u306E\u30B3\u30FC\u30C9\u306F\u305D\u306E\u307E\u307E\u3067\u306F\u5B9F\u884C\u3067\u304D\u306A\u3044\u306E\u3067\u3001\u4EE5\u4E0B\u30B3\u30DE\u30F3\u30C9\u3067Tweepy\u3001logservice-sdk\u3092\u30A4\u30F3\u30B9\u30C8\u30FC\u30EB\u5B9F\u884C\u3059\u308B\u5FC5\u8981\u304C\u3042\u308A\u307E\u3059\u3002    "), mdx("pre", null, mdx("code", {
